import React from 'react'
import Slider from "react-slick";
import 'slick-carousel/slick/slick.css'
import 'slick-carousel/slick/slick-theme.css'

function Header() {
    const settings = {
      dots: true,
      infinite: true,
      speed: 500,
      slidesToShow: 1,
      slidesToScroll: 1,
      autoplay: true,
      // fade: true,
      // initialSlide: 1,
      // lazyLoad: true,
      // autoplaySpeed: 3000,
    };

    const slider = [
      {
        "image":"/images/bg.webp",
        "p":"POUR PROFITER PLEINEMENT DE VOTRE DEMEURE.....CONFIEZ-MOI LA GESTION DES TÂCHES DE SECRÉTARIAT ET DE TENUE DE LIVRE DE VOTRE SYNDICAT DE COPROPRIÉTÉ",
      },
      {
        "image":"/images/bg.webp",
        "p":"POUR PROFITER PLEINEMENT DE VOTRE DEMEURE.....CONFIEZ-MOI LA GESTION DES TÂCHES DE SECRÉTARIAT ET DE TENUE DE LIVRE DE VOTRE SYNDICAT DE COPROPRIÉTÉ",
      },
      {
        "image":"/images/bg.webp",
        "p":"POUR PROFITER PLEINEMENT DE VOTRE DEMEURE.....CONFIEZ-MOI LA GESTION DES TÂCHES DE SECRÉTARIAT ET DE TENUE DE LIVRE DE VOTRE SYNDICAT DE COPROPRIÉTÉ",
      },
      {
        "image":"/images/bg.webp",
        "p":"POUR PROFITER PLEINEMENT DE VOTRE DEMEURE.....CONFIEZ-MOI LA GESTION DES TÂCHES DE SECRÉTARIAT ET DE TENUE DE LIVRE DE VOTRE SYNDICAT DE COPROPRIÉTÉ",
      }
    ]

  return (
    // <div className='header'>
        <Slider className='slider' {...settings}>
          {slider.map((item,key)=>(
            <div>
              <div className='bg'>
                <img src={item.image} />
              </div>
              <div className='content'>
                <p> {item.p} </p>
              </div>
            </div>
          ))}
        </Slider>
        // </div>
  )
}

export default Header
