import React, {useState} from 'react'
import Langs from "./languages/language.json"
import francais from "./languages/fr.json"

function Navbar() {
    const nav = [
        {
            "title":"Syndico",
            "link":""
        },
        {
            "title":"Module",
            "link":""
        },
        {
            "title":"Contact",
            "link":""
        },
    ]
    const [langid, setLangid] = useState(0)

    const [lang, setLang] = useState(false)
    let language

  return (
    <div>
        {lang?<div className='langs'>
            <ul>
            {Langs.map((lang,key)=>(
                <li onClick={() => setLangid(lang.id)} key={lang.id}><h4> {lang.sub} </h4> </li>
            ))}
            </ul>
        </div> : ""}

        {/* {language = langid} */}

        {langid === 0 &&( language = "francais")}
        {langid === 1 &&( language = "English")}
        {langid === 2 &&( language = "Arabe")}

        {language}

        <div className='navbar'>
            <div className="logo">
                <img src="/images/syndico.png" />
            </div>
            <ul>
                {nav.map((item,key)=>(
                    <li key={key+1}> <h3> {item.title} </h3> </li>
                ))}
                <li onClick={()=> setLang(!lang)}> <i className="fa-solid fa-globe"></i> </li>
            </ul>
        </div>


        {console.log(langid)}

        <div className='body'>
            {/* <h1> {Langs[langid].title} </h1> */}
        </div>

    </div>
  )
}

export default Navbar
