import React, {useState} from 'react'
import Langs from "./languages/language.json"
import Francais from "./languages/fr.json"
import Arabe from "./languages/ar.json"
import English from "./languages/en.json"

function Navbar() {
    const [langid, setLangid] = useState(0)

    const [lang, setLang] = useState(false)
    let Lang

  return (
    <div>
        {lang?<div className='langs'>
            <ul>
            {Langs.map((lang,key)=>(
                <li onClick={() => setLangid(lang.id)} key={lang.id}><h4> {lang.sub} </h4> </li>
            ))}
            </ul>
        </div> : ""}
        
        {/* <div className='navbar'>
            <div className="logo">
                <img src="/images/syndico.png" />
            </div>
            <ul>
                {langid === 0 && (Francais.navbar.map((item,key)=>(
                    <li key={key}> {item.title} </li>
                )))}
                {langid === 1 && (English.navbar.map((item,key)=>(
                    <li key={key}> {item.title} </li>
                )))}
                {langid === 2 && (Arabe.navbar.map((item,key)=>(
                    <li key={key}> {item.title} </li>
                )))}
                <li onClick={()=> setLang(!lang)}> <i className="fa-solid fa-globe"></i> </li>
            </ul>
        </div> */}


        <div className={`navbar ${langid===2 &&(rtl)}`}>
            <div className="logo">
                <img src="/images/syndico.png" />
            </div>
            <ul>
                {langid === 0 && (Francais.navbar.map((item,key)=>(
                    <li key={key}> {item.title} </li>
                )))}
                {langid === 1 && (English.navbar.map((item,key)=>(
                    <li key={key}> {item.title} </li>
                )))}
                {langid === 2 && (Arabe.navbar.map((item,key)=>(
                    <li key={key}> {item.title} </li>
                )))}
                <li onClick={()=> setLang(!lang)}> <i className="fa-solid fa-globe"></i> </li>
            </ul>
        </div>


        {console.log(langid)}

        <div className='body'>
            <h1> {Langs[langid].title} </h1>
        </div>

    </div>
  )
}

export default Navbar
