import React,{useState} from 'react'
import './App.css';
import './icons/css/all.min.css'
import Navbar from './components/Navbar'
import Carousel from './components/Carousel'
import './Navbar.css'
import langs from './data/language.json'
import fr from './data/fr.json'
import en from './data/en.json'
import ar from './data/ar.json'

function App() {

    const [ show, setShow] = useState(false)
    const [lang, setLang] = useState(0)

    return (
        <>
    {show?<div className='langs'>
        <ul>
            {langs.map((lang,key)=>(
                <li onClick={()=> setLang(lang.id)} key={key}> {lang.sub} </li>
            ))}
        </ul>
    </div>:""}

    <div className='navbar'>
        {lang==0 &&(
            <div className='ltr'>
            <div className='logo'>
                <img src="/images/syndico.png" />
            </div>
            <ul>
                {fr.navbar.map((item,key)=>(
                    <li key={key}> {item.title} </li>
                ))}
                <li onClick={()=> setShow(!show)}> <i className="fa-solid fa-globe"></i> </li>
            </ul>
            </div>
        )}
        {lang==1 &&(
            <div className='ltr'>
            <div className='logo'>
                <img src="/images/syndico.png" />
            </div>
            <ul>
                {en.navbar.map((item,key)=>(
                    <li key={key}> {item.title} </li>
                ))}
                <li onClick={()=> setShow(!show)}> <i className="fa-solid fa-globe"></i> </li>
            </ul>
            </div>
        )}
        {lang==2 &&(
            <div className='rtl'>
            <ul>
                <li onClick={()=> setShow(!show)}> <i className="fa-solid fa-globe"></i> </li>
                {ar.navbar.map((item,key)=>(
                    <li key={key}> {item.title} </li>
                ))}
            </ul>
            <div className='logo'>
                <img src="/images/syndico.png" />
            </div>
            </div>
        )}
    </div>

    </>
  )
}

export default App
